import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { issueAPI } from '../../services/api';
import '../../assets/styles/dashboard.css';

const Dashboard = () => {
  const [issues, setIssues] = useState([]);
  const [stats, setStats] = useState({ total: 0, pending: 0, inProgress: 0, resolved: 0 });

  useEffect(() => {
    loadIssues();
  }, []);

  const loadIssues = async () => {
    const response = await issueAPI.getAllIssues();
    const assignedIssues = response.data.filter(i => i.assignedTo);
    setIssues(assignedIssues);
    setStats({
      total: assignedIssues.length,
      pending: assignedIssues.filter(i => i.status === 'Pending').length,
      inProgress: assignedIssues.filter(i => i.status === 'In Progress').length,
      resolved: assignedIssues.filter(i => i.status === 'Resolved').length
    });
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Department Dashboard</h1>
        <p className="dashboard-subtitle">Manage assigned issues</p>
      </div>
      <div className="stats-grid">
        <div className="stat-card total"><div className="stat-label">Total Assigned</div><div className="stat-value">{stats.total}</div></div>
        <div className="stat-card pending"><div className="stat-label">Pending</div><div className="stat-value">{stats.pending}</div></div>
        <div className="stat-card in-progress"><div className="stat-label">In Progress</div><div className="stat-value">{stats.inProgress}</div></div>
        <div className="stat-card resolved"><div className="stat-label">Resolved</div><div className="stat-value">{stats.resolved}</div></div>
      </div>
      <div style={{ marginBottom: '2rem' }}><Link to="/department/update" className="btn btn-primary">Update Issue Status</Link></div>
      <div className="card">
        <h2 className="card-header">Assigned Issues</h2>
        {issues.length === 0 ? (
          <div className="empty-state"><div className="empty-state-icon">ðŸ“‹</div><p className="empty-state-text">No issues assigned yet</p></div>
        ) : (
          <div className="table-container">
            <table className="table">
              <thead><tr><th>ID</th><th>Category</th><th>Location</th><th>Status</th><th>Department</th><th>Date</th></tr></thead>
              <tbody>
                {issues.map(issue => (
                  <tr key={issue.id}>
                    <td>#{issue.id}</td>
                    <td>{issue.category}</td>
                    <td>{issue.location}</td>
                    <td><span className={`status-badge status-${issue.status.toLowerCase().replace(' ', '-')}`}>{issue.status}</span></td>
                    <td>{issue.assignedTo}</td>
                    <td>{issue.createdAt}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
