import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' }
});

let mockIssues = [
  { id: 1, category: 'Pothole', description: 'Large pothole on Main Street causing traffic issues', location: 'Main Street, Downtown', status: 'Pending', citizenId: 1, citizenName: 'John Doe', assignedTo: null, createdAt: '2024-01-15', image: null },
  { id: 2, category: 'Garbage Overflow', description: 'Garbage bin overflowing near City Park', location: 'City Park, North Area', status: 'In Progress', citizenId: 1, citizenName: 'John Doe', assignedTo: 'Sanitation Department', createdAt: '2024-01-14', image: null },
  { id: 3, category: 'Street Light', description: 'Street light not working on Oak Avenue', location: 'Oak Avenue, Block 5', status: 'Resolved', citizenId: 1, citizenName: 'John Doe', assignedTo: 'Electricity Department', createdAt: '2024-01-10', image: null },
  { id: 4, category: 'Water Leakage', description: 'Water pipe leaking continuously', location: 'Elm Street, Sector 3', status: 'Pending', citizenId: 1, citizenName: 'John Doe', assignedTo: null, createdAt: '2024-01-16', image: null }
];

export const issueAPI = {
  getAllIssues: async () => {
    return new Promise((resolve) => {
      setTimeout(() => resolve({ data: mockIssues }), 500);
    });
  },
  getIssuesByCitizen: async (citizenId) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const userIssues = mockIssues.filter(issue => issue.citizenId === citizenId);
        resolve({ data: userIssues });
      }, 500);
    });
  },
  createIssue: async (issueData) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const newIssue = {
          id: mockIssues.length + 1,
          ...issueData,
          status: 'Pending',
          assignedTo: null,
          createdAt: new Date().toISOString().split('T')[0]
        };
        mockIssues.push(newIssue);
        resolve({ data: newIssue });
      }, 500);
    });
  },
  updateIssueStatus: async (issueId, status) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const issue = mockIssues.find(i => i.id === issueId);
        if (issue) {
          issue.status = status;
          resolve({ data: issue });
        }
      }, 500);
    });
  },
  assignIssue: async (issueId, department) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const issue = mockIssues.find(i => i.id === issueId);
        if (issue) {
          issue.assignedTo = department;
          issue.status = 'In Progress';
          resolve({ data: issue });
        }
      }, 500);
    });
  },
  deleteIssue: async (issueId) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        mockIssues = mockIssues.filter(i => i.id !== issueId);
        resolve({ data: { message: 'Issue deleted successfully' } });
      }, 500);
    });
  }
};

export default api;
