import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { issueAPI } from '../../services/api';
import IssueCard from '../../components/IssueCard';
import '../../assets/styles/dashboard.css';

const MyIssues = () => {
  const { user } = useAuth();
  const [issues, setIssues] = useState([]);
  const [filter, setFilter] = useState('All');

  useEffect(() => {
    loadIssues();
  }, []);

  const loadIssues = async () => {
    const response = await issueAPI.getIssuesByCitizen(user.id);
    setIssues(response.data);
  };

  const filteredIssues = filter === 'All' ? issues : issues.filter(issue => issue.status === filter);

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">My Issues</h1>
        <p className="dashboard-subtitle">Track your reported issues</p>
      </div>
      <div style={{ marginBottom: '2rem' }}>
        <label style={{ marginRight: '1rem', fontWeight: '500' }}>Filter by Status:</label>
        <select className="form-select" value={filter} onChange={(e) => setFilter(e.target.value)} style={{ width: '200px', display: 'inline-block' }}>
          <option value="All">All Issues</option>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Resolved">Resolved</option>
        </select>
      </div>
      {filteredIssues.length === 0 ? (
        <div className="empty-state"><div className="empty-state-icon">ðŸ“‹</div><p className="empty-state-text">No issues found</p></div>
      ) : (
        <div className="issues-grid">{filteredIssues.map(issue => <IssueCard key={issue.id} issue={issue} />)}</div>
      )}
    </div>
  );
};

export default MyIssues;
