import React, { useState, useEffect } from 'react';
import { issueAPI } from '../../services/api';
import '../../assets/styles/dashboard.css';

const ManageIssues = () => {
  const [issues, setIssues] = useState([]);
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [department, setDepartment] = useState('');
  const [success, setSuccess] = useState('');

  const departments = ['Public Works Department', 'Sanitation Department', 'Electricity Department', 'Water Supply Department', 'Road Maintenance Department'];

  useEffect(() => {
    loadIssues();
  }, []);

  const loadIssues = async () => {
    const response = await issueAPI.getAllIssues();
    setIssues(response.data);
  };

  const handleAssign = async (issueId) => {
    if (!department) {
      alert('Please select a department');
      return;
    }
    await issueAPI.assignIssue(issueId, department);
    setSuccess('Issue assigned successfully!');
    setSelectedIssue(null);
    setDepartment('');
    loadIssues();
    setTimeout(() => setSuccess(''), 3000);
  };

  const handleDelete = async (issueId) => {
    if (window.confirm('Are you sure you want to delete this issue?')) {
      await issueAPI.deleteIssue(issueId);
      loadIssues();
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Manage Issues</h1>
        <p className="dashboard-subtitle">Assign issues to departments</p>
      </div>
      {success && <div className="alert alert-success">{success}</div>}
      <div className="card">
        <h2 className="card-header">All Issues</h2>
        <div className="table-container">
          <table className="table">
            <thead><tr><th>ID</th><th>Category</th><th>Description</th><th>Location</th><th>Status</th><th>Assigned To</th><th>Actions</th></tr></thead>
            <tbody>
              {issues.map(issue => (
                <tr key={issue.id}>
                  <td>#{issue.id}</td>
                  <td>{issue.category}</td>
                  <td>{issue.description.substring(0, 40)}...</td>
                  <td>{issue.location}</td>
                  <td><span className={`status-badge status-${issue.status.toLowerCase().replace(' ', '-')}`}>{issue.status}</span></td>
                  <td>{issue.assignedTo || 'Not Assigned'}</td>
                  <td>
                    <div className="table-actions">
                      {!issue.assignedTo && <button className="btn btn-primary btn-small" onClick={() => setSelectedIssue(issue.id)}>Assign</button>}
                      <button className="btn btn-danger btn-small" onClick={() => handleDelete(issue.id)}>Delete</button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {selectedIssue && (
        <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
          <div style={{ background: 'white', padding: '2rem', borderRadius: '8px', maxWidth: '500px', width: '100%' }}>
            <h3 style={{ marginBottom: '1rem' }}>Assign Issue #{selectedIssue}</h3>
            <div className="form-group">
              <label className="form-label">Select Department</label>
              <select className="form-select" value={department} onChange={(e) => setDepartment(e.target.value)}>
                <option value="">Choose a department</option>
                {departments.map(dept => <option key={dept} value={dept}>{dept}</option>)}
              </select>
            </div>
            <div style={{ display: 'flex', gap: '1rem', marginTop: '1.5rem' }}>
              <button className="btn btn-primary" onClick={() => handleAssign(selectedIssue)}>Assign</button>
              <button className="btn btn-secondary" onClick={() => { setSelectedIssue(null); setDepartment(''); }}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageIssues;
