import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();

  return (
    <nav className="navbar">
      <div className="navbar-content">
        <Link to="/" className="navbar-brand">
          Urban Issue Reporter
        </Link>

        <div className="navbar-menu">
          {!user ? (
            <>
              <Link to="/" className="navbar-link">Home</Link>
              <Link to="/about" className="navbar-link">About</Link>
              <Link to="/contact" className="navbar-link">Contact</Link>
              <Link to="/login" className="navbar-link">Login</Link>
              <Link to="/register" className="navbar-link">Register</Link>
            </>
          ) : (
            <>
              {user.role === 'citizen' && (
                <>
                  <Link to="/citizen/dashboard" className="navbar-link">Dashboard</Link>
                  <Link to="/citizen/report" className="navbar-link">Report Issue</Link>
                  <Link to="/citizen/my-issues" className="navbar-link">My Issues</Link>
                </>
              )}

              {user.role === 'admin' && (
                <>
                  <Link to="/admin/dashboard" className="navbar-link">Dashboard</Link>
                  <Link to="/admin/manage" className="navbar-link">Manage Issues</Link>
                </>
              )}

              {user.role === 'department' && (
                <>
                  <Link to="/department/dashboard" className="navbar-link">Dashboard</Link>
                  <Link to="/department/update" className="navbar-link">Update Status</Link>
                </>
              )}

              <Link to="/about" className="navbar-link">About</Link>
              <Link to="/contact" className="navbar-link">Contact</Link>

              <div className="navbar-user">
                <span className="navbar-link">Welcome, {user.name}</span>
                <button onClick={logout} className="btn-logout">Logout</button>
              </div>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
