import React, { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext();

const mockUsers = [
  { id: 1, email: 'citizen@test.com', password: 'citizen123', role: 'citizen', name: 'John Doe' },
  { id: 2, email: 'admin@test.com', password: 'admin123', role: 'admin', name: 'Admin User' },
  { id: 3, email: 'dept@test.com', password: 'dept123', role: 'department', name: 'Department Officer' }
];

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const login = (email, password) => {
    const foundUser = mockUsers.find(u => u.email === email && u.password === password);
    if (foundUser) {
      const userData = { ...foundUser };
      delete userData.password;
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      return { success: true, user: userData };
    }
    return { success: false, message: 'Invalid email or password' };
  };

  const register = (name, email, password) => {
    const existingUser = mockUsers.find(u => u.email === email);
    if (existingUser) {
      return { success: false, message: 'Email already registered' };
    }
    const newUser = { id: mockUsers.length + 1, email, name, role: 'citizen' };
    mockUsers.push({ ...newUser, password });
    setUser(newUser);
    localStorage.setItem('user', JSON.stringify(newUser));
    return { success: true, user: newUser };
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  return <AuthContext.Provider value={{ user, login, register, logout, loading }}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) throw new Error('useAuth must be used within AuthProvider');
  return context;
};
