import React, { useState } from 'react';
import '../styles/issues.css';

const Issues = () => {
  const [issues, setIssues] = useState([
    { id: 1, category: 'Pothole', description: 'Large pothole on Main Street', location: 'Main Street, Downtown', status: 'Pending', date: '2024-01-15' },
    { id: 2, category: 'Garbage Overflow', description: 'Garbage bin overflowing near City Park', location: 'City Park, North Area', status: 'In Progress', date: '2024-01-14' },
    { id: 3, category: 'Street Light', description: 'Street light not working', location: 'Oak Avenue, Block 5', status: 'Resolved', date: '2024-01-10' },
    { id: 4, category: 'Water Leakage', description: 'Water pipe leaking continuously', location: 'Elm Street, Sector 3', status: 'Pending', date: '2024-01-16' }
  ]);

  const [filter, setFilter] = useState('All');
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [newStatus, setNewStatus] = useState('');

  const filteredIssues = filter === 'All' ? issues : issues.filter(issue => issue.status === filter);

  const handleStatusUpdate = () => {
    if (selectedIssue && newStatus) {
      setIssues(issues.map(issue => 
        issue.id === selectedIssue ? { ...issue, status: newStatus } : issue
      ));
      setSelectedIssue(null);
      setNewStatus('');
    }
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this issue?')) {
      setIssues(issues.filter(issue => issue.id !== id));
    }
  };

  return (
    <div className="issues-container">
      <div className="issues-header">
        <h1 className="issues-title">Manage Issues</h1>
        <p className="issues-subtitle">View and manage all reported issues</p>
      </div>

      <div className="issues-filter">
        <label>Filter by Status:</label>
        <select className="form-select" value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="All">All Issues</option>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Resolved">Resolved</option>
        </select>
      </div>

      <div className="card">
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Category</th>
                <th>Description</th>
                <th>Location</th>
                <th>Status</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredIssues.map(issue => (
                <tr key={issue.id}>
                  <td>#{issue.id}</td>
                  <td>{issue.category}</td>
                  <td>{issue.description}</td>
                  <td>{issue.location}</td>
                  <td>
                    <span className={`status-badge status-${issue.status.toLowerCase().replace(' ', '-')}`}>
                      {issue.status}
                    </span>
                  </td>
                  <td>{issue.date}</td>
                  <td>
                    <div className="table-actions">
                      <button className="btn btn-primary btn-small" onClick={() => setSelectedIssue(issue.id)}>
                        Update
                      </button>
                      <button className="btn btn-danger btn-small" onClick={() => handleDelete(issue.id)}>
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {selectedIssue && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Update Issue Status</h3>
            <div className="form-group">
              <label className="form-label">Select New Status</label>
              <select className="form-select" value={newStatus} onChange={(e) => setNewStatus(e.target.value)}>
                <option value="">Choose status</option>
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Resolved">Resolved</option>
              </select>
            </div>
            <div className="modal-actions">
              <button className="btn btn-primary" onClick={handleStatusUpdate}>Update</button>
              <button className="btn btn-secondary" onClick={() => { setSelectedIssue(null); setNewStatus(''); }}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Issues;
