import React, { useState, useEffect } from 'react';
import { issueAPI } from '../../services/api';
import '../../assets/styles/dashboard.css';

const UpdateStatus = () => {
  const [issues, setIssues] = useState([]);
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [newStatus, setNewStatus] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadIssues();
  }, []);

  const loadIssues = async () => {
    const response = await issueAPI.getAllIssues();
    const assignedIssues = response.data.filter(i => i.assignedTo);
    setIssues(assignedIssues);
  };

  const handleUpdateStatus = async () => {
    if (!newStatus) {
      alert('Please select a status');
      return;
    }

    await issueAPI.updateIssueStatus(selectedIssue, newStatus);
    setSuccess('Status updated successfully!');
    setSelectedIssue(null);
    setNewStatus('');
    loadIssues();

    setTimeout(() => setSuccess(''), 3000);
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Update Issue Status</h1>
        <p className="dashboard-subtitle">Manage assigned issues</p>
      </div>

      {success && <div className="alert alert-success">{success}</div>}

      <div className="card">
        <h2 className="card-header">Assigned Issues</h2>
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Category</th>
                <th>Description</th>
                <th>Location</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {issues.map(issue => (
                <tr key={issue.id}>
                  <td>#{issue.id}</td>
                  <td>{issue.category}</td>
                  <td>{issue.description.substring(0, 40)}...</td>
                  <td>{issue.location}</td>
                  <td>
                    <span className={`status-badge status-${issue.status.toLowerCase().replace(' ', '-')}`}>
                      {issue.status}
                    </span>
                  </td>
                  <td>
                    <button 
                      className="btn btn-primary btn-small"
                      onClick={() => setSelectedIssue(issue.id)}
                    >
                      Update
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {selectedIssue && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            background: 'white',
            padding: '2rem',
            borderRadius: '8px',
            maxWidth: '500px',
            width: '100%'
          }}>
            <h3 style={{ marginBottom: '1rem' }}>Update Status for Issue #{selectedIssue}</h3>

            <div className="form-group">
              <label className="form-label">Select New Status</label>
              <select 
                className="form-select"
                value={newStatus}
                onChange={(e) => setNewStatus(e.target.value)}
              >
                <option value="">Choose status</option>
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Resolved">Resolved</option>
              </select>
            </div>

            <div style={{ display: 'flex', gap: '1rem', marginTop: '1.5rem' }}>
              <button 
                className="btn btn-success"
                onClick={handleUpdateStatus}
              >
                Update Status
              </button>
              <button 
                className="btn btn-secondary"
                onClick={() => {
                  setSelectedIssue(null);
                  setNewStatus('');
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UpdateStatus;
