import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { issueAPI } from '../../services/api';
import '../../assets/styles/dashboard.css';

const ReportIssue = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ category: '', description: '', location: '', image: null });
  const [success, setSuccess] = useState('');

  const categories = ['Pothole', 'Garbage Overflow', 'Street Light', 'Water Leakage', 'Drainage Issue', 'Road Damage', 'Other'];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    setFormData(prev => ({ ...prev, image: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const issueData = { ...formData, citizenId: user.id, citizenName: user.name };
    await issueAPI.createIssue(issueData);
    setSuccess('Issue reported successfully!');
    setTimeout(() => navigate('/citizen/my-issues'), 2000);
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Report New Issue</h1>
        <p className="dashboard-subtitle">Help us improve your city</p>
      </div>
      {success && <div className="alert alert-success">{success}</div>}
      <div className="form-section">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Issue Category *</label>
            <select name="category" className="form-select" value={formData.category} onChange={handleChange} required>
              <option value="">Select a category</option>
              {categories.map(cat => <option key={cat} value={cat}>{cat}</option>)}
            </select>
          </div>
          <div className="form-group">
            <label className="form-label">Description *</label>
            <textarea name="description" className="form-textarea" value={formData.description} onChange={handleChange} required placeholder="Describe the issue in detail..." />
          </div>
          <div className="form-group">
            <label className="form-label">Location *</label>
            <input type="text" name="location" className="form-input" value={formData.location} onChange={handleChange} required placeholder="Enter the location" />
          </div>
          <div className="form-group">
            <label className="form-label">Upload Image (Optional)</label>
            <input type="file" className="form-input" onChange={handleImageChange} accept="image/*" />
          </div>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <button type="submit" className="btn btn-primary">Submit Report</button>
            <button type="button" className="btn btn-secondary" onClick={() => navigate('/citizen/dashboard')}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ReportIssue;
